import { useState, useEffect } from "react"
import { useContext } from "react"
import { AuthContext } from '../../shared/context/auth-context';
import classes from './StartingPageContent.module.css'
import Card from './Card'




const HomePage = () => {
    const auth = useContext(AuthContext)
    const [job, setJob] = useState([])


    useEffect(() => {
        const fetchjob = async () => {
            try {
                const req = await fetch(
                    'http://localhost:1337/jobs', {
                    headers: {
                        'Content-Type': 'application/json',
                    }
                }
                )
                const data = await req.json();
                setJob(data.jobs)
            } catch (err) {
                console.log(err)
            }
        }
        fetchjob();
    }, []);

    const fetchInfo = async (event) => {

        let company = job.title

        try {
            event.preventDefault()
            const req = await fetch('http://localhost:1337/job:jid', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    title
                }),
            });
            const data = await req.json()

            if (!response.ok) {
                throw new Error(req.message)
            }
        } catch (err) {
            console.log(err)
        }
    }




    const jobInfo = job.map(job => <Card key={job.id} title={job.title || 'Searching...'} location={job.location} type={job.type} description={job.description} />)
    return (
        <section className={classes.background}>
            <div className={classes.search}>
                <label htmlFor='what'>What</label>
                <input
                    id='What'
                    placeholder='Job title, keywords, or company'
                />
                <label htmlFor='Where'>Where</label>
                <input
                    id='What'
                    placeholder='City, state, zip code, or "remote"'
                />
                <button>Find Jobs</button>
            </div>
            <div className={classes.cards}>
                <div onClick={fetchInfo} className={classes.card3}>
                    {jobInfo}
                </div>
                <div className={classes.card2}>
                    <div className={classes.sections}>
                        <h2>title</h2>
                        <h4>location</h4>
                        <button>Apply Now</button>
                    </div>
                    <div className={classes.sections}>
                        <h3>Job Details</h3>
                        <h6>Salary</h6>
                        <p>25$ an hour</p>
                        <h6>Job Type</h6>
                        <p>Full-Time</p>
                    </div>
                    <div className={classes.sections}>
                        <h2>Qualifications</h2>
                        <p>
                            In publishing and graphic design,
                            Lorem ipsum is a placeholder text commonly used to demonstrate
                            the visual form of a document or a typeface without relying on meaningful content.
                        </p>
                    </div>
                    <div className={classes.sections}>
