import { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { Button } from 'react-bootstrap';
// import { useContext } from "react"
// import { AuthContext } from '../../shared/context/auth-context';
// import { Link } from 'react-router-dom';
import classes from './StartingPageContent.module.css'
import Card from './Card'




const HomePage = () => {
    const [job, setJob] = useState([])
    const [info, setInfo] = useState([])
    const jobId = useParams().id;

    useEffect(() => {
        const fetchjob = async () => {
            try {
                const req = await fetch(
                    'http://localhost:1337/jobs', {
                    headers: {
                        'Content-Type': 'application/json',
                    }
                }
                )
                const data = await req.json();
                setJob(data.jobs)
            } catch (err) {
                console.log(err)
            }
        }
        fetchjob();
    }, []);


    useEffect(() => {
        const fetchInfo = async () => {
            try {
                const req = await fetch(`http://localhost:1337/job/${jobId}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                const data = await req.json();
                setInfo(data.job)
                if (!req.ok) {
                    throw new Error(req.message)
                }
            } catch (err) {
                console.log(err)
                console.log(jobId)
            }
        }
        fetchInfo()
    }, [jobId]);


    const jobInfo = job.map(job => <Card key={job.id} id={job.id} title={job.title || 'Searching...'} location={job.location} type={job.type} description={job.description} />)
    return (
        <section className={classes.background}>
            <div className={classes.search}>
                <label htmlFor='what'>What</label>
                <input
                    id='What'
                    className={classes.inputs}
                    placeholder='Job title or Keywords'
                />
                <label htmlFor='Where'>Where</label>
                <input
                    id='What'
                    className={classes.inputs}
                    placeholder='City, state, and zip code'
                />
                <Button>Find Jobs</Button>
            </div>
            <div className={classes.buttons}>
                <div className={classes.butt}>
                    <Button>View Applications</Button>
                </div>
                <div className={classes.butt}>
                    <Button>View Applicants</Button>
                </div>
            </div>
            <div className={classes.cards}>
                <div>
                    {jobInfo}
                </div>
                <div className={classes.card2}>
                    <div className={classes.sections}>
                        <h2>{info.title}</h2>
                        <h4>{info.location}</h4>
                        <Button>Apply Now</Button>
                    </div>
                    <div className={classes.sections}>
                        <h3>Job Details</h3>
                        <h6>Salary</h6>
                        <p>{info.salary}</p>
                        <h6>Job Type</h6>
                        <p>{info.type}</p>
                    </div>
                    <div className={classes.sections}>
                        <h2>Qualifications</h2>
                        <p>
                            {info.qualifications}
                        </p>
                    </div>
                    <div className={classes.sections}>
                        <h2>Full Job Description</h2>
                        <p>
                            {info.description}
                        </p>
                    </div>
                    <div>
                        <h2>Other</h2>
                        <p>
                            In publishing and graphic design,
                            Lorem ipsum is a placeholder text commonly used to demonstrate
                            the visual form of a document or a typeface without relying on meaningful content.
                        </p>
                    </div>
                </div>
            </div>
        </section >
    )
}

export default HomePage
