import classes from './JobDetail.module.css'
import { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { Button, Modal } from 'react-bootstrap';


const JobDetails = () => {
    const [info, setInfo] = useState([])
    const jobId = useParams().id;


    useEffect(() => {
        const fetchInfo = async () => {
            try {
                const req = await fetch(`http://localhost:1337/job/${jobId}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                const data = await req.json();
                setInfo(data.job)
                // let array = data.job.applicant;
                // for (let index in array) {
                //     setApp(array);
                //     console.log(job)
                // }
                if (!req.ok) {
                    throw new Error(req.message)
                }
            } catch (err) {
                console.log(err)
            }
        }
        fetchInfo()
    }, [jobId]);



    return (
        <section className={classes.page}>
            <div className={classes.parent}>
                <div className={classes.sections}>
                    <h1>{info.title || 'Searching'}</h1>
                    <h5>{info.location}</h5>
                    <Button >Apply Now</Button>
                    <Button>Update</Button>
                    <Button variant='danger'>Delete</Button>
                </div>
                <div className={classes.sections}>
                    <h3>Job Details</h3>
                    <h6>Salary</h6>
                    <p>{info.salary || 'Searching'}</p>
                    <h6>Job Type</h6>
                    <p>{info.type || 'Searching'}</p>
                </div>
                <div className={classes.sections}>
                    <h2>Qualifications</h2>
                    <p>
                        {info.qualifications || 'Searching'}
                    </p>
                </div>
                <div className={classes.section}>
                    <h2>Full Job Description</h2>
                    <p>
                        {info.description || 'Searching'}
                    </p>
                </div>
            </div>
        </section>
    )
}

export default JobDetails
