import { useContext } from 'react';
import { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import classes from './EditDetails.module.css'
import { Form } from 'react-bootstrap';
import { AuthContext } from '../../shared/context/auth-context';

const EditDetails = () => {
    // const navigate = useNavigate()
    // const auth = useContext(AuthContext)
    const [info, setInfo] = useState("")
    const [title, setTitle] = useState('')
    const [location, setLocation] = useState(`${info.location}`)
    const [salary, setSalary] = useState("")
    const [type, setType] = useState("")
    const [qualifications, setQualifications] = useState("")
    const [description, setDescription] = useState("")
    const jobId = useParams().id;
    // const userId = auth.userId;

    useEffect(() => {
        const fetchInfo = async () => {
            try {
                const req = await fetch(`http://localhost:1337/job/${jobId}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                const data = await req.json();
                setInfo(data.job)
                setTitle(JSON.stringify(data.job))
                // let array = data.job.applicant;
                // for (let index in array) {
                //     setApp(array);
                //     console.log(job)
                // }
                if (!req.ok) {
                    throw new Error(req.message)
                }
            } catch (err) {
                console.log(err)
            }
        }
        fetchInfo()
    }, [jobId]);


    const editJob = async (event) => { }

    return (
        <section className={classes.page} >
            <div className={classes.auth}>
                <h1>Edit Job</h1>
                <Form onSubmit={editJob}>
                    <Form.Group className={classes.control} >
                        <Form.Label>Company Name</Form.Label>
                        <Form.Control
                            value={title}
                            type='text'
                            onChange={(e) => setTitle(e.target.value)}
                            required />
                    </Form.Group>
                    <Form.Group className={classes.control}>
                        <Form.Label>Location</Form.Label>
                        <Form.Control
                            value={location}
                            type='text'
                            onChange={(e) => setLocation(e.target.value)}
                            required />
                    </Form.Group>
                    <Form.Group className={classes.control}>
                        <Form.Label>Salary</Form.Label>
                        <Form.Control
                            value={salary}
                            type='text'
                            onChange={(e) => setSalary(e.target.value)}
                            required />
                    </Form.Group>
                    <Form.Group className={classes.control}>
                        <Form.Label>Job Type</Form.Label>
                        <Form.Control
                            value={type}
                            type='text'
                            onChange={(e) => setType(e.target.value)}
                            required />
                    </Form.Group>
                    <Form.Group className={classes.control}>
                        <Form.Label>Qualifications</Form.Label>
                        <Form.Control
                            value={qualifications}
                            as="textarea"
                            onChange={(e) => setQualifications(e.target.value)}
                            required />
                    </Form.Group>
                    <Form.Group className={classes.control}>
                        <Form.Label>Description</Form.Label>
                        <Form.Control
                            value={description}
                            as="textarea"
                            onChange={(e) => setDescription(e.target.value)}
                            required />
                    </Form.Group>
                    <div className={classes.actions}>
                        <button>Submit</button>
                    </div>
                </Form>
            </div>
        </section >
    )
}


export default EditDetails
