import React, { useState, useEffect, useCallback } from "react";
import { useParams } from 'react-router-dom';
import { Button, Modal } from 'react-bootstrap';
import { useContext } from "react"
import { AuthContext } from '../../shared/context/auth-context';
import { Link } from 'react-router-dom';
import classes from './StartingPageContent.module.css'
import Card from './Card'
// import List from "./List";




const HomePage = () => {
    const auth = useContext(AuthContext)
    const [show, setShow] = useState(false);
    const [show2, setShow2] = useState(false);
    const [job, setJob] = useState([])
    const [info, setInfo] = useState([])
    const [user, setUser] = useState([])
    const [what, setWhat] = useState('')
    // const [app, setApp] = useState({})
    const jobId = useParams().id;
    const userId = auth.userId;

    const handleClose = () => setShow(false);
    const handleClose2 = () => setShow2(false);
    const handleShow = () => setShow(true);
    const handleShow2 = () => setShow2(true);








    // const appInfo = application.append(application => <List name={application.title} />)

    useEffect(() => {
        const fetchjob = async () => {
            try {
                const req = await fetch(
                    'http://localhost:1337/jobs', {
                    headers: {
                        'Content-Type': 'application/json',
                    }
                }
                )
                const data = await req.json();
                setJob(data.jobs)
            } catch (err) {
                console.log(err)
            }
        }
        fetchjob();
        filterJobs(what, jobInfo)
    }, []);


    useEffect(() => {
        const fetchInfo = async () => {
            try {
                const req = await fetch(`http://localhost:1337/job/${jobId}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                const data = await req.json();
                setInfo(data.job)
                // let array = data.job.applicant;
                // let i
                // for (i = 0; i < array.length; i++) {
                //     setApp(array)
                // }

                if (!req.ok) {
                    throw new Error(req.message)
                }
            } catch (err) { }
        }
        fetchInfo()
    }, [jobId]);

    const Apply = useCallback(async () => {
        let name = user.name
        let email = user.email
        let age = user.age
        let number = user.number
        let experience = user.experience
        try {
            const req = await fetch(`http://localhost:1337/job/${jobId}/apply`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name,
                    email,
                    age,
                    number,
                    experience
                }),
            })
            if (req.ok) {
                alert('successfully Applied')
            }
            if (!req.ok) {
                alert('something went wrong')
                throw new Error(req.message)
            }
        } catch (err) {
            console.log(err)
        }
    }, [jobId, user.age, user.email, user.experience, user.name, user.number])


    useEffect(() => {
        const fetchUser = async () => {
            try {
                const req = await fetch(`http://localhost:1337/user/${userId}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                const data = await req.json();
                setUser(data.users)
                if (!req.ok) {
                    throw new Error(req.message)
                }
            } catch (err) { }
        }
        fetchUser()
    }, [userId, Apply]);




    const jobInfo = job.map(job =>
        <Card
            key={job.id}
            id={job.id}
            title={job.title}
            salary={job.salary}
            location={job.location}
            type={job.type}
            description={job.description}
        />)

    const filterJobs = (value, data) => {
        let filteredData = []
        console.log('trying2')
        for (let i = 0; i < data.length; i++) {
            value = value.toLowerCase()
            const name = data[i].props.title
            console.log(name)

            if (name.includes(value)) {
                filteredData.push(data[i].props)
                setJob(filteredData)
                console.log(filteredData)
                console.log(job)
            }
        }
    }

    const onFilter = () => {
        filterJobs(what, jobInfo)
    }



    return (
        <section className={classes.background}>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* {appInfo} */}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
            <Modal show={show2} onHide={handleClose2}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading2</Modal.Title>
                </Modal.Header>
                <Modal.Body></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose2}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose2}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>

            <div className={classes.search}>
                <form>
                    <label htmlFor='what'>What</label>
                    <input
                        id='What'
                        className={classes.inputs}
                        value={what}
                        onChange={(e) => setWhat(e.target.value)}
                        placeholder='Job title or Keywords'
                    />

                    <label htmlFor='Where'>Where</label>
                    <input
                        id='Where'
                        className={classes.inputs}
                        placeholder='City, state, and zip code'
                    />
                    <Button onClick={onFilter}>Find Jobs</Button>
                </form>
            </div>
            {auth.isLoggedIn && (
                <div className={classes.buttons}>
                    <div className={classes.butt}>
                        <Button onClick={handleShow}>Applied To</Button>
                    </div>
                    <div className={classes.butt}>
                        <Button onClick={handleShow2}>View Applicants</Button>
                    </div>
                </div>
            )}
            <div className={classes.cards}>
                <div>
                    {jobInfo}
                </div>
                <div className={classes.card2}>
                    <div className={classes.sections}>
                        <h3>{info.title || 'Select a job'}</h3>
                        <h5>{info.location}</h5>
                        {auth.isLoggedIn && (
                            <Button onClick={Apply}>Apply Now</Button>
                        )}
                        {!auth.isLoggedIn && (
                            <Link to='/auth'>
                                <Button>Apply Now</Button>
                            </Link>
                        )}
                    </div>
                    <div className={classes.sections}>
                        <h3>Job Details</h3>
                        <h6>Salary</h6>
                        <p>{info.salary || 'Searching...'}</p>
                        <h6>Job Type</h6>
                        <p>{info.type || 'Searching...'}</p>
                    </div>
                    <div className={classes.sections}>
                        <h2>Qualifications</h2>
                        <p>
                            {info.qualifications || 'Searching...'}
                        </p>
                    </div>
                    <div>
                        <h2>Full Job Description</h2>
                        <p>
                            {info.description || 'Searching...'}
                        </p>
                    </div>
                </div>
            </div>
        </section >
    )


}
export default HomePage
