import classes from './JobDetail.module.css'
import { useState, useEffect, useContext } from "react";
import { useParams, Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../shared/context/auth-context';
import { Button, Modal } from 'react-bootstrap';
import List from '../StartingPage/List';

const JobDetails = () => {
    const auth = useContext(AuthContext)
    const navigate = useNavigate()
    const [info, setInfo] = useState([])
    const [show, setShow] = useState(false);
    const [app, setApp] = useState([])

    const jobId = useParams().id;

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    useEffect(() => {
        const fetchInfo = async () => {
            try {
                const req = await fetch(`http://localhost:1337/job/${jobId}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                const data = await req.json();
                setInfo(data.job);
                setApp(data.job.applicant);
                if (!req.ok) {
                    throw new Error(req.message)
                }
            } catch (err) {
                console.log(err)
            }
        }
        fetchInfo()
    }, [jobId]);


    const deleteJob = async () => {
        console.log(info.title)
        try {
            const req = await fetch(`http://localhost:1337/delete`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: 'Bearer ' + auth.token
                }, body: JSON.stringify({
                    name: info.title
                }),
            });
            navigate('/')
            if (!req.ok) {
                throw new Error(req.message)
            }
        } catch (err) {
            console.log(err)
        }
    }
    console.log(app)

    const appInfo = app.map(({ name }) => {
        return <List
            key={Math.random()}
            title={name}
        />
    })

    return (
        <section className={classes.page}>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Applicants</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {appInfo}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="danger" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
            <div className={classes.parent}>
                <div className={classes.sections}>
                    <h1>{info.title || 'Searching'}</h1>
                    <h5>{info.location}</h5>
                    <Button >Apply Now</Button>
                    <Link to={`/details/edit/${jobId}`}>
                        <Button>Update</Button>
                    </Link>
                    <Button variant='danger' onClick={deleteJob}>Delete</Button>
                    <Button variant='primary' onClick={handleShow}>Applicants</Button>
                </div>
                <div className={classes.sections}>
                    <h3>Job Details</h3>
                    <h6>Salary</h6>
                    <p>{info.salary || 'Searching'}</p>
                    <h6>Job Type</h6>
                    <p>{info.type || 'Searching'}</p>
                </div>
                <div className={classes.sections}>
                    <h2>Qualifications</h2>
                    <p>
                        {info.qualifications || 'Searching'}
                    </p>
                </div>
                <div className={classes.section}>
                    <h2>Full Job Description</h2>
                    <p>
                        {info.description || 'Searching'}
                    </p>
                </div>
            </div>
        </section>
    )
}

export default JobDetails
