import classes from './Card.module.css'
import { Link } from 'react-router-dom';
import { useState } from 'react';


const Card = (props) => {
    const [name, setName] = useState('')

    // const onSetName = () => {
    //     setName(h3)
    // }

    const fetchInfo = async (event) => {
        try {
            event.preventDefault()
            const req = await fetch(`http://localhost:1337/job`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            const data = await req.json()
            if (!req.ok) {
                throw new Error(req.message)
            }
        } catch (err) {
            console.log(err)
        }
    }


    props.onFetchJob(name)

    return (
        <div as={Link} to='auth' className={classes.card}>
            {/* <Link to='/auth'></Link> */}
            <div onClick={fetchInfo}>
                <h3>{props.title}</h3>
            </div>
            <p>{props.location}</p>
            <p>{props.type}</p>
            <p>{props.description}</p>
        </div>
    )

}


export default Card
